rray('jquery'));?><script type="text/javascript">    jQuery('#search_product_banner1 , #search_product_banner2 , #search_product_banner3').keyup(function(){        var id = $(this).attr('id');        id = id.replace("search_product_banner", "");        var  v = jQuery(this).val();        if(v.length >= 3){            jQuery("#loading_product" + id).removeClass("hide");            jQuery.ajax({                url: ajaxurl,                data: {                    action: "load_all_product",                    search_product : v                },                success:function(data) {                    jQuery("#loading_product"+id).addClass("hide");                    jQuery("#value_post_items_menu"+id).html(data);                }            });        }    });</script>